function GetText(selector) {
    if (window.debugger != null)
        debugger ;
    var result = [];
    if ($(selector).length == 0) {
        result.push(true);
    }

    for (var i=0;i<2;i++) {
        if ($(selector).eq(i).text() == "") {
            result.push(false);
        } else {
            result.push(true);
        }
    }
    return result;
}

