		//todo : manage for popup
	}	


	betHalf()
	{
		$$(".amount__right button")[0].click();
	}

	betDouble(){
		$$(".amount__right button")[1].click();
	}

	bet()
	{
		this.learn();
		$1("button.btn.btn_blue.btn_large.btn_block").click();
	}

	getFirstCard()
	{
		let cardElt = $$(".card.isFlipped div.card-inner")[0];
		let value = cardElt.textContent;
		let color = cardElt.querySelector(".card-back").classList[1].split("_")[1];
		return new Card(value,color);
	}


	learn(c = null)
	{
		let card = typeof c == 'undefined' ? this.getFirstCard() : c;
		let same = JSON.stringify(this.deck[this.deck.length - 1]) == JSON.stringify(card);
		if (!same){
			this.deck.push(card);
		}
		localStorage.deck = JSON.stringify(this.deck);
	}

	show()
	{
		this.deck.join();
	}
}

