class Freebitcoin extends Faucet
{
	tries = 0;
	constructor()
	{
		super();
	}
	
	logout()
	{
		$(".logout_link").click();
	}

	
	redeem()
	{
		console.group("Roll redeem");
		//console.log("roll ask to be redeemed in",this.counter.asSeconds());
		if (!isSelectorVisible(settings.rollButtonSelector)) {
			console.info("the button roll is not shown so next...");
			return;
		}
		
		console.info("the button roll is available to click...");
		

		
		console.info("No captchas on the page.");
		
		if (this.counter.asSeconds() == 0)
		{		
			console.info("Time to roll...so");
			//todo : rajouter un compteur pour actualiser si le captcha stuck
			
			if (this.reCaptchaShown())
			{
				if($(settings.switchCaptchaButtonSelector).length > 0)
					$(settings.switchCaptchaButtonSelector).click();
				else
							sendEmail(MAIL_SECURITY_TOKEN,settings.adminAccountUrl,settings.adminAccountUrl,"Action requise" ,"Besoin de passer le recaptcha pour l'instance_" + game.party.user.fieldLocationRow);
				return;
			}
				
			if (this.doubleCaptchaShown())
			{
				if (this.tries <= 3){
					solver.ask2AllCaptcha();
					console.info("Captcha is solving....");
					var solveCaptchaInterval = setInterval(() => {
						if($(settings.captchaResponse).eq(0).val() != "" && $(settings.captchaResponse).eq(1).val() != "")
						{
							clearInterval(solveCaptchaInterval);
							this.roll();
						}
						else
						{
							console.log("	Solving again...");
						}
					},1000);
				}
				else
				{
					$("#main_content").css("backgroundColor","pink");
					sendEmail(MAIL_SECURITY_TOKEN,settings.adminAccountUrl,settings.adminAccountUrl,"Action requise" ,"Mode manuel pour instance_" + game.party.user.fieldLocationRow);
				}
			}
			else
			{
				console.info("Rolling!!");
				this.roll();
			}
		}
	}
	
	roll()
	{
		if (game.party.user.cryptoBalance >= 0.00012000)
		{
			console.info("Redeem lottery");
			this.doesRedeemLottery();
		}
		console.info("Captchas need to be resolved...");
		console.info("Redeem bonus");
		this.doesRedeemBonus();
		console.log("Time to roll");
		setTimeout(() => $(settings.rollButtonSelector).click(), randomInteger(5000));
		console.info("waiting for success..");
		var txtBox = document.querySelectorAll(settings.captchaResponse);
		$(txtBox)[0].css("backgroundColor","white");
		$(txtBox)[1].css("backgroundColor","white");
		this.waitBeforeChangePageTimer = setInterval(() => {
				if(isSelectorVisible(settings.successSelector)) 
				{
					this.tries = 0;
					$("#main_content").css("backgroundColor","white");
					console.info("Success rolling!");	
					{
						clearInterval(this.waitBeforeChangePageTimer);
						console.info("Update sheets");
						game.party.user.getLastCryptoGain();
						persistance.UpdateOnRoll();
						persistance.WriteComment("");
						setTimeout(() => window.location.reload(),randomInteger(60000));
					}
				}
				else if (isSelectorVisible(settings.failedSelector))
				{
					++this.tries;
					clearInterval(this.waitBeforeChangePageTimer);
					let requestIdsFailed = solver.jsonReturns.map(e => e.request).join();
					console.error(`Failed rolling for`,solver.jsonReturns);
					let bodyMail = `Le combo de captcha immatriculé ${requestIdsFailed} a échoué.`;
					
					sendEmail(MAIL_SECURITY_TOKEN,settings.adminAccountUrl,settings.adminAccountUrl,"Echec résolution captcha",bodyMail);
					persistance.WriteComment(bodyMail);
					setTimeout(() => this.redeem(),randomInteger(60000));
				}
			},1000);			
	}
	
	
	isAnyCaptcha()
	{
		return this.doubleCaptchaShown() || this.reCaptchaShown();
	}
	
	
	reCaptchaShown() { 
		return isSelectorReady(settings.recaptchaSelector) && isSelectorVisible(settings.recaptchaSelector);
	}
	
	doubleCaptchaShown()
	{
		return isSelectorReady(settings.doubleCaptchaSelector) && isSelectorVisible(settings.doubleCaptchaSelector);
	}
	
	doesRedeemLottery()
	{
		if(game.party.user.getAccountUnblockTickets() > 0)
		{
			game.lottery.redeem();
		}
	}
	
	doesRedeemBonus()
	{
		$(settings.selectors).each((i,b) => {
			game.party.user.bonusActives = game.party.user.getActiveBonus();
			if (typeof(game.party.user.bonusActives[b]) == 'undefined')
			{
				let bonus = game.bonus.find(e => e.selector == b);
				if (typeof(bonus) != 'undefined')
					setTimeout(() => bonus.redeem(b) ,1000)
			}})
	}

	eachMinutes()
	{
		
	}
}

