class Solver
{
	solverDomain = "";
	captchaSolverKey = "ea7f60647bc74057705601eb957e420b";//"6vjf2cw3zhxpvpdlzfqrcg7qbkktmn98";

	
	
	solveTimeout = [];
	jsonReturns = [];
	digests = [];
	
	constructor()
	{
		this.digests = [];
		switch (settings.solverReference)
		{
			case "AZ":
				this.solverDomain = 'http://azcaptcha.com';
				this.captchaSolverKey = "6vjf2cw3zhxpvpdlzfqrcg7qbkktmn98";
				break;
			case "2C":
				this.solverDomain = 'http://discount.2captcha.com';//'http://azcaptcha.com';
				this.captchaSolverKey = "ea7f60647bc74057705601eb957e420b";
				break;			
		}
	}
	
	ask2Captcha(i,renew){
		let e = document.querySelectorAll(settings.captchaCanvasSelector)[i];
		let dataUrl = "";
    	var calculateNewCanvas = () => {
	    		let e = document.querySelectorAll(settings.captchaCanvasSelector)[i];
	    		var waitForLoadCanvas = setInterval(() =>{
	    		dataUrl = getDataUrl(e);
	    		digestMessage(dataUrl).then((data) => 
	    		{
		    		if (this.digests.indexOf(data) > -1)
		    		{
		    			console.error("Ce canvas a dÃ©ja Ã©tÃ© envoyÃ© pour analyse");
		    			clearInterval(waitForLoadCanvas);
		    			return;
		    		}
						    			
						    			
					if (dataUrl.length > 778)
					{
						this.digests[i] = data;
						clearInterval(waitForLoadCanvas);
						var data = {
				            key: this.captchaSolverKey,
				            body: getDataUrl(e),
				            method:"base64",
				            json:1,

				            textinstructions:"please enter the 6 letters that you see in the box below,please not 0 or 1"
				        };
				        
				        if (settings.solverReference == "2C")
				        {
				        	data["numeric"] = 2;
				        	data["min_len"] = 6;
				        	data["max_len"] = 6;
				        }
				        
						var txtBox = document.querySelectorAll(settings.captchaResponse)[i];
				        $(txtBox).css("backgroundColor","white");
				        
						$.post(`${this.solverDomain}/in.php`, data)
				        .done((data) => {
				        	if (data.request == "ERROR_WRONG_FILE_EXTENSION")
							{
								console.log("Encore ce problème d'extension de fichier erroné!");								
								return;
							}
				        	data = settings.solverReference == "AZ" ? JSON.parse(data) : data;
				        	$(txtBox).css("backgroundColor","pink");
				        	this.jsonReturns[i]= data;console.log(this.jsonReturns);    
				        	this.solveTimeout[i] = setTimeout(() => this.resolve2Captcha(data.request), 20000);
				        })
					}
	    		})
			},1000)
		}; 		
		
		
		if (renew)
		{
			this.digests.splice(i,1);
			var captchaBox = e.parentElement.parentElement;
			var txtBox = document.querySelectorAll(settings.captchaResponse)[i];
			$(txtBox).css("backgroundColor","white");
			var dataPreviousUrl = getDataUrl(e);
			let iteration = 0;
			captchaBox.querySelector("p.captchasnet_captcha_change_p.captchasnet_captcha_refresh i").dispatchEvent(new MouseEvent("click",{bubbles: true, cancellable: true}));

			setTimeout(() => calculateNewCanvas(),15000);
		}
		else
		{
			calculateNewCanvas();
		}
	}
	
	
	
	
	ask2AllCaptcha() {
		this.jsonReturns = [];
	    $(settings.captchaCanvasSelector).each((i,e) => {
	    	this.ask2Captcha(i,false);
	    });
	}
	
	getCaptchaIndex(requestId)
	{
		return this.jsonReturns.findIndex((e) => e.request == requestId);
	}
	
	resolve2Captcha(requestId) {
			$.get(`${this.solverDomain}/res.php?key=${this.captchaSolverKey}&action=get&id=${requestId}&json=1`).done((response) => 
			{
				response = settings.solverReference == "AZ" ? JSON.parse(response) : response;
				var captchaIndex = this.getCaptchaIndex(requestId);
				
				//TODO : implement https://2captcha.com/setting/errors
				// implement this.jsonReturns.splice(captchaTextIndex,1);
				let isValidCaptcha = settings.captchaRegEx.test(response.request);
				if (response.request == "CAPCHA_NOT_READY")
				{
					setTimeout(() => this.resolve2Captcha(requestId),5000);
					return;
				}
				else if (response.request == "ERROR_CAPTCHA_UNSOLVABLE" || !isValidCaptcha) 
				{
					this.ask2Captcha(captchaIndex,true);
					return;
				}
				if (typeof(captchaIndex) == 'undefined')
				{
					console.log("requestId not found");
					return;
				}
				
				this.jsonReturns[captchaIndex].result = response;
				if (response.request.length != 6)
				{
					this.ask2Captcha(captchaIndex,true);			
					return;
				}
				$(settings.captchaResponse).eq(captchaIndex).val(response.request);
			})
	}
}

