class User
{
	email = "";
	rewardPoints = 0;
	cryptoBalance = 0.0;
	cryptoAddress = "";
	fieldLocationRow = 0;
	blockingTickets = 0;
	blockingTimer = 0;
	bonusActives = [];
	oldBlockingTickets = 0;
	oldCryptoBalance = 0;
	oldRewardPoints = 0;
	blockingSatoshis = 0;
	
	lastGainRewardPoints = 0;
	lastGainBtc = 0;
	lastGainLottery = 0;
	
	constructor()
	{
		if (settings.hasBonus)
		{
			this.bonusActives = this.getActiveBonus();
		}
		
		this.email = this.getProfileMail();
		
		this.cryptoBalance = this.getCryptoBalance();
		
		if (this.cryptoBalance <= 0.00000004)
			if (!$("#disable_lottery_checkbox").is(":checked"))
				$("#disable_lottery_checkbox").click();
		this.cryptoAddress = this.getCryptoAddress();
		this.fieldLocationRow = this.email == settings.adminAccountUrl ? 2 : this.getCellNumber();
		this.fieldLocationRow = settings.excludedMails.indexOf(this.email) > -1 ? 102 : this.fieldLocationRow;
		this.rewardPoints = this.getRpPoints();
		
		this.blockingTimer = setInterval(() => {
			if(isSelectorReady(settings.blockingTicketsSelector))
			{
				clearInterval(this.blockingTimer);
				this.blockingTickets = this.getAccountUnblockTickets();
			}
		},1000);
		
		//init old values
		this.oldRewardPoints = this.rewardPoints;
		this.oldCryptoBalance = this.cryptoBalance;
		this.oldBlockingTickets = this.blockingTickets;
		
		this.lastGainLottery = this.getLastLottery();
		this.lastGainRewardPoints = this.getLastRewardsPoints();
		this.getLastCryptoGain();
	}
	
	getLastCryptoGain()
	{
		var btcWin = $(settings.lastCryptoGainSelector).text();
		this.lastGainBtc = parseFloat(btcWin != "" ? btcWin : $1(settings.minCryptoGain).textContent.substr(0,10));
		return this.lastGainBtc;
	}
	
	getLastLottery()
	{
		return $(settings.lastLotteryGainSelector).text();
	}
	getLastRewardsPoints()
	{
		return $(settings.lastRewardPointsGainSelector).text();
	}
	
	getActiveBonus(){
		var activeBonus = $("#reward_points_bonuses_main_div").find(".free_play_bonus_box_span_large:nth-child(1)");
		//let bonusActivated = [{'lottery':''}];
		let bonusActivated = [];
		//Some active bonus
		if (activeBonus.length > 0)
		{
			$(activeBonus).each((i,e) => {
				var id = $(e).closest("div").attr("id").replace("bonus_container_","");
				var multiplicateur = settings.redeems[id][2];
				var item = $(e).text().replace(/[ _$&%a-zA-Z.+-]/g,'');
				var amountActive = parseInt(item);
				if (!isNaN(amountActive))
				{
					bonusActivated[id] = amountActive * multiplicateur;
				}
			})
		}
		return bonusActivated;
	}
	
	getCryptoBalance() {
		return parseFloat($(settings.cryptoBalanceSelector).text().trim().substring(0,10)) 
	};

	getCryptoAddress() {return $(settings.cryptoAddressSelector).val() };
	
	getProfileMail() { return $(settings.profileMailSelector).val()};
	
	getRpPoints() { 
		 return $(settings.rewardPointsSelector).text().replace(/,|\./g,'');
	}
	getCellNumber() {
		let compteNumber = parseInt(this.email.split('@')[0].replaceAll(/[_a-zA-Z@\.-]/g,''));
		compteNumber = compteNumber > 1000 ? 4 : compteNumber;
		return +compteNumber + 2;
	}
	
	getAccountUnblockTickets() {
		this.blockingSatoshis = 0;
		let accountUnblockTickets =  $(settings.blockingTicketsSelector).html();
		accountUnblockTickets = typeof accountUnblockTickets == "undefined" || accountUnblockTickets == "" ? 0 : accountUnblockTickets.replace(/,|\./,'');
		if (accountUnblockTickets == 0)
			return 0;
		accountUnblockTickets = parseInt(accountUnblockTickets);	
		this.blockingSatoshis = parseFloat(accountUnblockTickets * 0.00000001);			
		return accountUnblockTickets;			
	}
	
	
	
}




