class Counter
{
	seconds = 0;
	minutes = 0;
	hours = 0;
	selector = "";
	timeRemaining = "";
	constructor(selector)
	{
		this.selector = selector;
	}

	toString(){return this.hours + ":" + (this.minutes == "" ? "00" : this.minutes) + ":" + (this.seconds  == "" ? "00" : this.seconds);}
	
	asSeconds_(){return moment.duration(this.toString()).asSeconds()};
	asSeconds() { 
		let currentSeconds = 0;
		this.toString().split(":").reverse().forEach((e,i) => currentSeconds += e * Math.pow(60,i));
		return currentSeconds;
	};
	
	countDown(){
		console.log("countdown called");
		//CountDown(this.selector,this.asSeconds());
	}
	
	
	
}
