class BonusCounter extends Counter
{
	randomWaiting;
	timer = 0;
	duration;
	jQuerySelector = "";
	constructor(selector)
	{
		super(selector);
		this.jQuerySelector = "#bonus_span_" + this.selector;
		this.randomWaiting = randomInteger(60000);
		console.log("class",selector,"created");
		this.duration = new TimeSpan();
		setInterval(() => {this.timer = this.getBonusTimer(this.jQuerySelector)}, 1000);
