		    if(isSelectorFilled(selector)) {
		        clearInterval(this.timer);
		        this.timeRemaining = $(selector).text().replace(/[a-zA-Z]/g,'');
		        [this.duration.hours, this.duration.minutes,this.duration.seconds] = this.timeRemaining.split(":");
		    }
	}

	getHours(){ return this.hours; }
	getMinutes(){ return this.minutes; }
	getSeconds(){ return this.seconds; }
}



