class TimeZone
{
	timestart = null;
	timeend = null;
	
	constructor(hourInterval)
	{
		
		this.timestart = this.HourAsIsoDate(hourInterval.low);	
		this.timeend = this.HourAsIsoDate(hourInterval.high);
	}
	
	
	HourAsIsoDate(hour)
	{ 
		return new Date(new Date().toISOString().substring(0,11) + hour)
	}
	
	isInInterval(hour) {return this.HourAsIsoDate(hour).getTime() < this.timeend.getTime() && this.HourAsIsoDate(hour).getTime() > this.timestart.getTime()};
}



