function createRandomText(len = 7,seed = CHARSET)
{
	let chain = "";
	for(i=0;i<=len;i++) {
		chain = chain.concat(seed[Math.floor((Math.random() * seed.length))]);
	}
	return chain;
}

function createMutationObserver(selector)
{
	// Selectionne le noeud dont les mutations seront observées
	var targetNode = document.querySelector(selector);

	// Options de l'observateur (quelles sont les mutations à observer)
	var config = { attributes: true, childList: true };
	
	// Fonction callback à éxécuter quand une mutation est observée
	var callback = function(mutationsList) {
	    for(var mutation of mutationsList) {
	        if (mutation.type == 'childList') {
	            console.log('Un noeud enfant a été ajouté ou supprimé.');
	        }
	        else if (mutation.type == 'attributes') {
	            console.log("L'attribut '" + mutation.attributeName + "' a été modifié.");
	        }
	    }
	};
	
	// Créé une instance de l'observateur lié à la fonction de callback
	var observer = new MutationObserver(callback);
	
	// Commence à observer le noeud cible pour les mutations précédemment configurées
	observer.observe(targetNode, config);
	
	// L'observation peut être arrêtée par la suite
	observer.disconnect();
}


function getAjax(url, success) {
    var xhr = window.XMLHttpRequest ? new XMLHttpRequest() : new ActiveXObject('Microsoft.XMLHTTP');
    xhr.open('GET', url);
    xhr.onreadystatechange = function() {
        if (xhr.readyState>3 && xhr.status==200) success(xhr.responseText);
    };
    xhr.setRequestHeader('X-Requested-With', 'XMLHttpRequest');
    xhr.send();
    return xhr;
}

function postAjax(url, data, success) {
    var params = typeof data == 'string' ? data : Object.keys(data).map(
            function(k){ return encodeURIComponent(k) + '=' + encodeURIComponent(data[k]) }
        ).join('&');

    var xhr = window.XMLHttpRequest ? new XMLHttpRequest() : new ActiveXObject("Microsoft.XMLHTTP");
    xhr.open('POST', url);
    xhr.onreadystatechange = function() {
        if (xhr.readyState>3 && xhr.status==200) { success(xhr.responseText); }
    };
    xhr.setRequestHeader('X-Requested-With', 'XMLHttpRequest');
    xhr.setRequestHeader('Content-Type', 'application/x-www-form-urlencoded');
    xhr.setRequestHeader('pragma','no-cache');
    xhr.setRequestHeader('cache-control', 'max-age=0');
    xhr.send(params);
    return xhr;
}

// helper for enabling IE 8 event bindings
function addEvent(el, type, handler) {
    if (el.attachEvent) el.attachEvent('on'+type, handler); else el.addEventListener(type, handler);
}

// live binding helper
function live(selector, event, callback, context) {
    addEvent(context || document, event, function(e) {
        var found, el = e.target || e.srcElement;
        while (el && !(found = el.id == selector)) el = el.parentElement;
        if (found) callback.call(el, e);
    });
}


async function digestMessage(message) {
  const msgUint8 = new TextEncoder().encode(message);                           // encode comme (utf-8) Uint8Array
  const hashBuffer = await crypto.subtle.digest('SHA-256', msgUint8);           // fait le condensÃ©
  const hashArray = Array.from(new Uint8Array(hashBuffer));                     // convertit le buffer en tableau d'octet
  const hashHex = hashArray.map(b => b.toString(16).padStart(2, '0')).join(''); // convertit le tableau en chaÃ®ne hexadÃ©limale
  return hashHex;
}


var createCanvas = (img) => {
	// Create canvas
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');
    // Set width and height and origin to anonymous
    $(img).attr("crossOrigin","Anonymous");
    canvas.width = img.width;
    canvas.height = img.height;
    // Draw the image
    ctx.drawImage(img, 0, 0);
    return canvas;
}


