var buildCounters = () => 
{
	if (typeof(localStorage.sites) == 'undefined')
		return;
	var j = JSON.parse(localStorage.sites);
	Object.keys(j.sites).forEach(item => {
		console.log(`Counter for timer ${item} adding`);
		buildCounter(item,j.sites[item]);
	})
}


var buildCounter = (key,value) => 
{
	var elt = $1(`#${key}`);
	if (elt != null)
		return;



	var container = $(`<div id='${key}container' class='timer hasCountdown'>`);
	$(`#${key}container`).after(`<span>${key}</span>`);
	$("#wait").after(container);
	CountDown(`#${key}container`,value);
}


var reloadSnapshot = () => 
{
	if (typeof(localStorage.sites) == 'undefined')
		return;
	var j = JSON.parse(localStorage.sites);
	Object.keys(j.sites).forEach(item => {
		console.log(`Canvas for timer ${item} adding`);
		buildImage(item,j.sites[item])
	}
	);
}


var buildImage = (key,value) =>
{
	if (value.length < 500)
		return;

	var elt = $1(`#${key}`);
	if (elt == null)
	{
		var container = $(`<div id='${key}container' class='timer'></div>`);
		var img = dataURLToImage(value);
		img.id = key;
		$(container).append(img);
		$(container).append(`<figcaption align="center">${key}</figcaption>`);
		$("#wait").after(container);
	}
	else
	{
		if (value != elt.src)
			elt.src = value;	
	}
}
