class TimeZone
{
	timestart = null;
	timeend = null;

	constructor(hourInterval)
	{
		this.timestart = HourAsIsoDate(hourInterval.low);	
		this.timeend = HourAsIsoDate(hourInterval.high);
	}


	isInInterval(hour) {return HourAsIsoDate(hour).getTime() < this.timeend.getTime() && HourAsIsoDate(hour).getTime() > this.timestart.getTime()};
}

