function FireAtInterval(selector,interval)
{
	clearInterval(timers[selector]);
	var duration = randomInteger(interval);
	console.log("Timer started for " + selector + ":" + duration + "ms" );
	if (selector != "roll"){
		var collected = FindFirstLower(rpNeeds,userRpPoints) / 12;
		if (collected > 0)
		{
			setTimeout(Redeem("free_points_" + collected),duration);
		}

		userRpPoints -= collected;
		collected = FindFirstLower(rpNeeds,userRpPoints) / 12;
		if (collected > 0)
		{
			setTimeout(Redeem("fp_bonus_" + collected),duration);
		}

		userRpPoints -= collected;
		collected = FindFirstLower(rpNeeds,userRpPoints) / 12;
		if (collected > 0)
		{
			setTimeout(Redeem("free_lott_" + collected),duration);
		}		
	}
	else {
		if ($("#play_without_captchas_button").length == 0)
			setTimeout(RollClick(),duration);
	}
}



