class Solver
{
	captchaSolverKey = "ea7f60647bc74057705601eb957e420b";
	solveTimeout = [];
	jsonReturns = [];
	digests = [];
	
	constructor()
	{
		this.digests = [];
	}
	
	ask2Captcha(i,e,renew){
		if (renew)
			GenerateCaptchasNetCaptcha('botdetect_free_play_captcha' + i == 0 ? "" : "2", 3);
	    	var waitForLoadCanvas = setInterval(() =>{
	    		var dataUrl = getDataUrl(e);
	    		digestMessage(dataUrl).then((data) => 
	    		{
		    		if (this.digests.indexOf(data) > -1)
		    		{
		    			console.error("Ce canvas a dÃ©ja Ã©tÃ© envoyÃ© pour analyse");
		    			clearInterval(waitForLoadCanvas);
		    			return;
		    		}
						    			
						    			
					if (dataUrl.length > 778)
					{
						this.digests[i] = data;
						clearInterval(waitForLoadCanvas);
						
						$.post("http://discount.2captcha.com/in.php", {
				            key: this.captchaSolverKey,
				            body: getDataUrl(e),
				            method:"base64",
				            json:1,
				            numeric:2,
				            min_len:6,
				            max_len:6,
				            textinstructions:"please enter the 6 letters that you see in the box below,please not 0 or 1"
				        })
				        .done((data) => {
				        	this.jsonReturns[i]= data;console.log(this.jsonReturns);    
				        	this.solveTimeout[i] = setTimeout(() => this.resolve2Captcha(data.request), 20000);
				        })
					}
	    		})
			},1000); 
	}
	
	
	ask2AllCaptcha() {
		this.jsonReturns = [];
	    $("div.captchasnet_captcha_content img").each((i,e) => {
	    	this.ask2Captcha(i,e,false);
	    });
	}
	
	getCaptchaIndex(requestId)
	{
		return this.jsonReturns.findIndex((e) => e.request == requestId);
	}
	
	resolve2Captcha(requestId) {
			$.get(`http://discount.2captcha.com/res.php?key=${this.captchaSolverKey}&action=get&id=${requestId}&json=1`).done((response) => 
			{
				var captchaIndex = this.getCaptchaIndex(requestId);
				//TODO : implement https://2captcha.com/setting/errors
				// implement this.jsonReturns.splice(captchaTextIndex,1);
				let isValidCaptcha = settings.captchaRegEx.test(response.request);
				if (response.request == "ERROR_CAPTCHA_UNSOLVABLE" || !isValidCaptcha) 
				{
					//clean digests
					//this.digests.splice(captchaIndex,1);
					
					//prevents for error
					/*
					$.get(`http://discount.2captcha.com/res.php?key=${this.captchaSolverKey}&action=reportbad&id=${requestId}`);
					*/
					this.ask2Captcha(captchaIndex,$("div.captchasnet_captcha_content img").eq(captchaIndex)[0],true);
				}
				if (response.status == 0) setTimeout(() => this.resolve2Captcha(requestId),5000);
				if (typeof(captchaIndex) == 'undefined')
				{
					console.log("requestId not found");
					return;
				}
				/*
				$.get(`http://discount.2captcha.com/res.php?key=${this.captchaSolverKey}&action=reportgood&id=${requestId}`);
				*/
				$(".captchasnet_captcha_input_box").eq(captchaIndex).val(response.request.length == 6 ? response.request : "");
			})
	}
}


