var intervalRef = 60000;
var stoppedInstances = [];
const STOPPED = "stopped";
const RUNNING = "success";
const ACTIONS = {STOP:"stop",REBOOT:"reboot",START:"start"};
var stuckedInstances = [];
var timersInstances = [];
//var isJqueryLoaded = () =>  {return jQuery !== null;}


	console.log("oui");
/*	
    while (typeof ($) === 'undefined') {
        setInterval(isJqueryLoaded, 1)
    }
*/
 
    var randomInteger = (max) => 
	{
		var wait = Math.floor(Math.random() * max);
		console.log(wait);
		return wait;
	}
    
    
    var getInstancesStucked = async () => {
    	stuckedInstances = [];
    	stuckedInstances = await sheetAsJson("https://spreadsheets.google.com/feeds/cells/1BvbccqgHuG6-XJ9KIJIoD_sE9gAKA9iZ3XXonvd1VCg/4/public/full?alt=json","toReset",null);
    	return stuckedInstances;
    }
    
    var getInstancesStopped = () => {return $("tr[pan-data-name]").find(".p6n-icon-status[status='stopped']")};
    
    function getInstanceName(instance) { 
    	return $(instance).parent().parent().find("a").html();
    }
    
    var getInstanceStatus = (instance) => {
    	return $(instance).find("gce-status-tooltip").parent().find(".p6n-icon-status").attr("status")
    }
    
    var checkInstanceStatus = (instanceName) => {
    	return getInstanceByName(instanceName).parent().find(".p6n-icon-status").attr("status");
    }
    
    var getInstanceByName = (instanceName) => {
    	return $("tr[pan-data-name]").find("gce-status-tooltip").parent().find("a").filter((i,e) => $(e).html() == instanceName);
    }
    
    var actInstance = (instance,action) => {
    	//cocher
    	$(instance).closest("tr").find("td.p6n-col-checkbox").click();
    	console.log("instance",instance,"is checked");
    	//act on instance
    	$(`.p6n-icon-${action}`).click();
    	console.log(action,"on	instance",instance,"done");
    }

    var manageInstancesTimer = setInterval(() => {
    	timersInstances = [];
    	stoppedInstances = getInstancesStopped();
    	getInstancesStucked().then(
    	(instances) => {
		stuckedInstances = instances;    		
    	$(stoppedInstances).each((i,e) => {
    		var instanceName = getInstanceName(e);
    		timersInstances[instanceName] = {"interval":0,"started":false,"stopped":false};
    		timersInstances[instanceName].interval = setInterval(() => {
    			if (!timersInstances[instanceName].started)
    			{
    				actInstance(e,ACTIONS.START);
    				timersInstances[instanceName].started = true;
    			if (stoppedInstances.length > 0)
    				stoppedInstances.pop();
    			}
    			if(checkInstanceStatus(instanceName) == RUNNING)
    			{
    				clearInterval(timersInstances[instanceName]);
    			}
    		},100);
    	})
    	//bypass second part
    	//instances = [];
    	$(stuckedInstances).each((i,e) => {
    			manageSheetInstance(i,e);
    		})
    	})
    },randomInteger(intervalRef * 2))
    
    var manageSheetInstance = (i,e) => {
		timersInstances[e] = {"interval":0,"started":false,"stopped":false};
			timersInstances[e].interval = setInterval(() => {
			if (!timersInstances[e].stopped)
			{
				actInstance(getInstanceByName(e),ACTIONS.STOP);
				timersInstances[e].stopped = true;
				stuckedInstances = stuckedInstances.reverse();
				if (stuckedInstances.length > 0)
					stuckedInstances.pop();
			}
			if(checkInstanceStatus(e) == STOPPED)
			{
				clearInterval(timersInstances[e]);
			}
		},timersInstances[e].stopped ? 300000 : 60000);
    }
